    private void Get_Customer_InformationActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_Get_Customer_InformationActionPerformed
try {
                  Connection c = null;
	         Statement stmt = null;
	         Class.forName("org.postgresql.Driver");
	         c = DriverManager
	            .getConnection("jdbc:postgresql://localhost:5432/HouseBoat",
	            "postgres", "893510");
		      stmt = c.createStatement();
                      String Customer_Id = Customer_ID.getText();
                      String sql = "select * from Customer ";
                      ResultSet rs=stmt.executeQuery(sql);
                      while(rs.next()){
                          Phone_Number.setText(rs.getString("phone_no"));
                          Customer_Name.setText(rs.getString("customer_name"));
                          Email.setText(rs.getString("email"));
                      }
                      rs.close();
                      stmt.close();
                      c.close();
	      } 
	      catch (Exception e) {
	         e.printStackTrace();
	         System.err.println(e.getClass().getName()+": "+e.getMessage());
	         System.exit(0);
	      }          // TODO add your handling code here:
    }//GEN-LAST:event_Get_Customer_InformationActionPerformed

