    private void UpadteActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_UpadteActionPerformed
try {
                  Connection c = null;
	         Statement stmt = null;
	         Class.forName("org.postgresql.Driver");
	         c = DriverManager
	            .getConnection("jdbc:postgresql://localhost:5432/HouseBoat",
	            "postgres", "893510");
		      stmt = c.createStatement();
                      String Customer_Id = Customer_ID.getText();
                      
                      String sql = "select * from Boat";
                      ResultSet rs=stmt.executeQuery(sql);
                      while(rs.next()){
                          String name = rs.getString("boat_name");
                          Boat_Name.addItem(name);
                          int capacity = rs.getInt("capacity");
                          for(int i=0;i<capacity;i++){
                              Decks.addItem(Integer.toString(capacity-i));
                          }
                          
                      }
                      rs.close();
                      stmt.close();
                      c.close();
	      } 
	      catch (Exception e) {
	         e.printStackTrace();
	         System.err.println(e.getClass().getName()+": "+e.getMessage());
	         System.exit(0);
	      }           // TODO add your handling code here:
    }//GEN-LAST:event_UpadteActionPerformed

